package model.physicsMIT;import java.lang.Double; // import statement added to mollify javadocimport java.util.Iterator;import model.physicsMIT.Geometry.DoublePair;import model.physicsMIT.Geometry.VectPair;/** * This is the interface that the singleton Geometry dispatches to. * Callers will probably wish to use the singleton Geometry in most * cases. * * @see model.physicsMIT.Geometry **/public interface GeometryInterface {  /**   * Specified by Geometry.quadraticSolution   *   * @see model.physicsMIT.Geometry#quadraticSolution   **/  public DoublePair quadraticSolution(double a, double b, double c);  /**   * Specified by Geometry.minQuadraticSolution   *   * @see model.physicsMIT.Geometry#minQuadraticSolution   **/  public double minQuadraticSolution(double a,				     double b,				     double c);  /**   * Specified by Geometry.perpendicularPoint   *   * @see model.physicsMIT.Geometry#perpendicularPoint   **/  public Vect perpendicularPoint(LineSegment line,				 Vect point);  /**   * Specified by Geometry.perpendicularPointWholeLine   *   * @see model.physicsMIT.Geometry#perpendicularPointWholeLine   **/  public Vect perpendicularPointWholeLine(LineSegment line,					  Vect point);  /**   * Specified by Geometry.applyReflectionCoeff   *   * @see model.physicsMIT.Geometry#applyReflectionCoeff   **/  public Vect applyReflectionCoeff(Vect incidentVect,				   Vect reflectedVect,				   double rCoeff);  /**   * Specified by Geometry.timeUntilWallCollision   *   * @see model.physicsMIT.Geometry#timeUntilWallCollision   **/  public double timeUntilWallCollision(LineSegment line,				       Circle ball,				       Vect velocity);  /**   * Specified by Geometry.reflectWall   *   * @see model.physicsMIT.Geometry#reflectWall   **/  public Vect reflectWall(LineSegment line,			  Vect velocity,			  double reflectionCoeff);  /**   * Specified by Geometry.reflectWall   *   * @see model.physicsMIT.Geometry#reflectWall   **/  public Vect reflectWall(LineSegment line,			  Vect velocity);  /****************************************************************************   *   * METHODS FOR CIRCLES   *   ***************************************************************************/  /**   * Specified by Geometry.distanceSquared   *   * @see model.physicsMIT.Geometry#distanceSquared   **/  public double distanceSquared(Vect v1, Vect v2);  /**   * Specified by Geometry.distanceSquared   *   * @see model.physicsMIT.Geometry#distanceSquared   **/   public double distanceSquared(double x1, double y1,				 double x2, double y2);  /**   * Specified by Geometry.timeUntilCircleCollision   *   * @see model.physicsMIT.Geometry#timeUntilCircleCollision   **/  public double timeUntilCircleCollision(Circle circle,					 Circle ball,					 Vect velocity);  /**   * Specified by Geometry.reflectCircle   *   * @see model.physicsMIT.Geometry#reflectCircle   **/  public Vect reflectCircle(Vect circle,			    Vect ball,			    Vect velocity, 			    double reflectionCoeff);  /**   * Specified by Geometry.reflectCircle   *   * @see model.physicsMIT.Geometry#reflectCircle   **/  public Vect reflectCircle(Vect circle,			    Vect ball,			    Vect velocity);  /****************************************************************************   *   * METHODS FOR ROTATING LINE SEGMENTS AND CIRCLES   *   ***************************************************************************/  /**   * Specified by Geometry.rotateAround   *   * @see model.physicsMIT.Geometry#rotateAround   **/  public Vect rotateAround(Vect point, Vect cor, Angle a);  /**   * Specified by Geometry.rotateAround   *   * @see model.physicsMIT.Geometry#rotateAround   **/  public LineSegment rotateAround(LineSegment line, Vect cor, Angle a);  /**   * Specified by Geometry.rotateAround   *   * @see model.physicsMIT.Geometry#rotateAround   **/  public Circle rotateAround(Circle circle, Vect cor, Angle a);  /**   * Specified by Geometry.timeUntilCircleCollision   *   * @see model.physicsMIT.Geometry#timeUntilCircleCollision   **/  public DoublePair timeUntilCircleCollision(Circle circle,					     Vect point,					     Vect velocity);  /**   * Specified by Geometry.timeUntilRotatingWallCollision   *   * @see model.physicsMIT.Geometry#timeUntilRotatingWallCollision   **/  public double timeUntilRotatingWallCollision(LineSegment line,					       Vect center,					       double angularVelocity,					       Circle ball,					       Vect velocity);  /**   * Specified by Geometry.reflectRotatingWall   *   * @see model.physicsMIT.Geometry#reflectRotatingWall   **/  public Vect reflectRotatingWall(LineSegment line,				  Vect center,				  double angularVelocity,				  Circle ball,				  Vect velocity);  /**   * Specified by Geometry.reflectRotatingWall   *   * @see model.physicsMIT.Geometry#reflectRotatingWall   **/  public Vect reflectRotatingWall(LineSegment line,				  Vect center,				  double angularVelocity,				  Circle ball,				  Vect velocity,				  double reflectionCoeff);  /**   * Specified by Geometry.timeUntilRotatingCircleCollision   *   * @see model.physicsMIT.Geometry#timeUntilRotatingCircleCollision   **/  public double timeUntilRotatingCircleCollision(Circle circle,						 Vect center,						 double angularVelocity,						 Circle ball,						 Vect velocity);  /**   * Specified by Geometry.reflectRotatingCircle   *   * @see model.physicsMIT.Geometry#reflectRotatingCircle   **/  public Vect reflectRotatingCircle(Circle circle,				    Vect center,				    double angularVelocity,				    Circle ball,				    Vect velocity);  /**   * Specified by Geometry.reflectRotatingCircle   *   * @see model.physicsMIT.Geometry#reflectRotatingCircle   **/  public Vect reflectRotatingCircle(Circle circle,				    Vect center,				    double angularVelocity,				    Circle ball,				    Vect velocity,				    double reflectionCoeff);  /****************************************************************************   *   * METHODS FOR MULTI-BALL SIMULATIONS   *   ***************************************************************************/  /**   * Specified by Geometry.timeUntilBallBallCollision   *   * @see model.physicsMIT.Geometry#timeUntilBallBallCollision   **/  public double timeUntilBallBallCollision(Circle ball1,					   Vect   vel1,					   Circle ball2,					   Vect   vel2);  /**   * Specified by Geometry.reflectBalls   *   * @see model.physicsMIT.Geometry#reflectBalls   **/  public VectPair reflectBalls(Vect center1,			       double mass1,			       Vect velocity1,			       Vect center2,			       double mass2,			       Vect velocity2);}